mapper.select:
SELECT

FROM x_kv
WHERE project = #{project}
AND profile = #{profile}
ORDER BY id

SELECT

FROM x_kv
WHERE project in

    #{project}

AND profile = #{profile}
ORDER BY id

SELECT

FROM x_kv
WHERE project = #{project}
AND profile =  #{profile}
ORDER BY project,id

mapper.update:
UPDATE x_kv
SET xValue = #{xValue},security = #{security},description = #{description}
WHERE project = #{project}
AND profile = #{profile}
AND xKey = #{xKey}

